import axios from "axios";
import "../login/login.css";
import { useContext, useState } from 'react';
import { useNavigate } from "react-router-dom";
import Swal from 'sweetalert2';
import {AuthContext} from "../../context/AuthContext";


const Login=()=>{
    const [username, setUsername] = useState()
    const [password, setPassword] = useState()
    const [loading, setLoading] = useState(false)
    const navigate = useNavigate();
    const {user, dispatch} = useContext(AuthContext);

    const handleLogin = async(e) =>{
        e.preventDefault()
        setLoading(true)
        const data = {
            username,
            password,
        }

        await axios.post("http://localhost:8000/api/auth/login", data)
        .then((res) => {
            setLoading(false)

            if(res.status === 200){
                dispatch({type:"LOGIN_SUCCESS", payload:res.data});
            user = res.data;
            console.log(user);
               return navigate('/home')
            }
            else{
               Swal.fire(
                   'Login',
                   res.data.message,
                   'error'
               )
            }
        })
        .catch(err => {
            setLoading(false)
            dispatch({type:"LOGIN_FAILURE", payload: err});
            Swal.fire(
                'Login',
                "Opps something went wrong",
                'error'
            )
        })


    }

    return(
        <div style={{display: "flex", backgroundColor:"gray", flexDirection: "column", justifyContent: "center", alignitems: "center", height: "100vh"}}>
            <div className="loginContainer">
            <h2>Login</h2>
            <form className="loginForm" onSubmit={handleLogin}>
                <div className="loginEdit">
                    <label>Username</label>
                    <input value={username} onChange={(e) => setUsername(e.target.value)} type="text" placeholder="E.g. Isotope"/>
                </div>
                <div className="loginEdit">
                    <label>Password</label>
                    <input value={password} onChange={(e) => setPassword(e.target.value)} type="password" placeholder="*********"/>
                </div>
                <button type="submit" disabled={loading}>{loading ? 'Loading...' : 'Login'}</button>
            </form>
        </div>
        </div>

    );
}

export default Login;
