import axios from "axios";
import "../login/login.css"
import { useState } from 'react'
import { useNavigate } from "react-router-dom";
import Swal from 'sweetalert2'
import { useContext } from "react";
import { AuthContext } from "../../context/AuthContext";
import res from "express/lib/response";


const Login=()=>{
    const [username, setUsername] = useState()
    const [password, setPassword] = useState()
    const navigate = useNavigate();

    const {loading, error, dispatch} = useContext(AuthContext);
    const handleLogin = async(e) =>{
        e.preventDefault()
        setLoading(true)
        const data = {
            username,
            password,
        }
        dispatch({type:"LOGIN_START"})
        try{
            await axios.post("http://localhost:8000/api/auth/login", data);
            dispatch({type:"LOGIN_SUCCESS", payload: res.data});
        }catch(err){
            dispatch({type:"LOGIN_FAILURE", payload: res.data});}


    }

    return(
        <div style={{display: "flex", backgroundColor:"gray", flexDirection: "column", justifyContent: "center", alignitems: "center", height: "100vh"}}>
            <div className="loginContainer">
            <h2>Login</h2>
            <form className="loginForm" onSubmit={handleLogin}>
                <div className="loginEdit">
                    <label>Username</label>
                    <input id={username} onChange={(e) => setUsername(e.target.value)} type="text" placeholder="E.g. Isotope"/>
                </div>
                <div className="loginEdit">
                    <label>Password</label>
                    <input id={password} onChange={(e) => setPassword(e.target.value)} type="password" placeholder="*********"/>
                </div>
                <button type="submit" disabled={loading}>{loading ? 'Loading...' : 'Login'}</button>
            </form>
        </div>
        </div>

    );
}

export default Login;
