import React, { useState } from 'react'
import '../sign_up/sign_up.css'
import backG from '../../images/back.jpeg'
import axios from 'axios'
import Swal from 'sweetalert2'
import { useNavigate } from 'react-router-dom'

export default function Sign_Up() {
    const [username, setUsername] = useState(undefined)
    const [password, setPassword] = useState(undefined)
    const [email, setEmail] = useState(undefined)
    const [city, setCity] = useState(undefined)
    const [phone, setPhone] = useState(undefined)
    const [country, setCountry] = useState(undefined)
    const [image, setImage] = useState();
    const [loading, setLoading] = useState(false)
    const navigate = useNavigate();

    const handleImage = (e) =>{
        const [file] = e.target.files;
        setImage(URL.createObjectURL(file));
    }
    const handleSign = async(e) =>{
        e.preventDefault()
        setLoading(true)
        const data = {
            username,
            password,
            email,
            city,
            phone,
            country,
            image
        }

        await axios.post("http://localhost:8000/api/auth/sign", data).then((res) => {
            setLoading(false)
            if(res.status === 200){
                console.log(res.data)
            localStorage.setItem("user", JSON.stringify(res.data.details));
               return navigate('/home')
            }
            else{
               Swal.fire(
                   'Login',
                   res.data.message,
                   'error'
               )
            }
        }).catch(err => {
            setLoading(false)
            Swal.fire(
                'Login',
                "Opps something went wrong",
                'error'
            )
        });


    }


  return (
      <div style={{backgroundImage: `url(${backG})`,display: "flex", flexDirection: "column", justifyContent: "center", alignitems: "center", height: "100vh",  backgroundSize: "cover"}}>


    <div className='sign-container' >
        <h2>
            Sign Up
        </h2>
        <div className='sign-items'>
            <div className='img-container'>
            {image==null?<div className='img'>
            </div>:<img src={image} alt=" Profile pic" className='img'/>}
            <input type="file" onChange={handleImage}/></div>
            <div className='sign-input'>
                <span>Username</span>
             <input type="text" placeholder='Username'/></div>
            <div className='sign-input'>
                <span>Email</span>
             <input type="text" placeholder='Email'/></div>
            <div className='sign-input'>
                <span>Country</span>
             <input type="text" placeholder='Country'/></div>
            <div className='sign-input'>
                <span>City</span>
             <input type="text" placeholder='City'/></div>
            <div className='sign-input'>
                <span>Phone</span>
             <input type="number" placeholder='Phone'/></div>
            <div className='sign-input'>
                <span>Password</span>
            <input type="text" placeholder='Password'/></div>

            <button onClick={handleSign} disabled={loading}>{loading ? 'Loading...' : 'Sign Up'}</button>
        </div>
    </div>
    </div>
  )
}
